CREATE TABLE `system_user`(
	`id` 			BIGINT(20) 	NOT NULL AUTO_INCREMENT COMMENT '编号',
	`name` 			VARCHAR(20) 	NOT NULL DEFAULT '' COMMENT '姓名',
	`sex` 			TINYINT(4) 	NOT NULL DEFAULT '0' COMMENT '性别 0-未知 1-男 2-女',
	`age` 			TINYINT(4) 	NOT NULL DEFAULT '0' COMMENT '年龄',
	`remark` 		VARCHAR(200) 	NOT NULL DEFAULT '' COMMENT '备注',
	`create_userid` 	BIGINT(20) 	NOT NULL DEFAULT '0' COMMENT '创建人ID',                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
        `create_username` 	VARCHAR(50) 	NOT NULL DEFAULT '' COMMENT '创建人姓名',                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
        `create_time` 		TIMESTAMP 	NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
        `update_userid` 	BIGINT(20) 	NOT NULL DEFAULT '0' COMMENT '更新人ID',                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
        `update_username` 	VARCHAR(50) 	NOT NULL DEFAULT '' COMMENT '更新人',                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
        `update_time` 		TIMESTAMP 	NOT NULL DEFAULT '2000-01-01 00:00:00' COMMENT '更新时间',
        `is_deleted` 		TINYINT(1) 	NOT NULL DEFAULT '0' COMMENT '是否删除',
        `sys_time` 		TIMESTAMP 	NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '系统时间',
        PRIMARY KEY (`id`)               

)ENGINE=INNODB DEFAULT CHARSET=utf8 COMMENT='用户表';   

